// main.js
import { createApp } from 'vue';
import App from './App.vue';
import router from './router'; // 引入刚才创建的路由实例

// 引入 element-plus 组件库
import { use } from 'element-plus/dist/vue-mjs';
import 'element-plus/dist/index.css';

// 创建并配置 Vue 应用
const app = createApp(App);

// 注册全局路由
app.use(router);

// 使用 element-plus
app.use(use);

// 挂载应用到 #app 元素
app.mount('#app');
